
public class HelloWorld {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//输出常量
		//输出十进制整数
		System.out.println("输出十进制整数    " + 125);
		//输出十进制浮点数
		System.out.println("输出十进制浮点数  " + 0.01);
		//输出二进制整数
		System.out.println("输出二进制整数    " + 0b100);
		//输出八进制整数
		System.out.println("输出八进制整数    " + 0100);
		//输出十六进制整数
		System.out.println("输出十六进制整数  " + 0x100);
		//输出布尔值
		System.out.println("输出布尔值       " + true);
		//输出字符串
		System.out.println("输出字符串       " + "HelloWorld");
		//输出字符
		System.out.println("输出字符         " + 'H');
	}
}
